definition.script:
podTemplate(
    containers: [
        containerTemplate(name: 'node', image: 'node:latest', ttyEnabled: true, command: 'cat')
        ]
    ){
        node(POD_LABEL){
            container('node'){
                  sh 'git clone https://github.com/PharmaLedger-IMI/governance-workspace.git'
                  sh 'cd governance-workspace && git log -n 1 --format="%H" > usecase.version && cat usecase.version'
                  script{
                    //usecase_version = sh(  returnStdout: true, script: 'cat governance-workspace/usecase.version')
                    usecase_version = sh(  returnStdout: true, script: 'cd governance-workspace && git log -n 1 --format="%H"')
                  }
                  sh "echo '${usecase_version}'"
                  sh 'echo end'
            }

        }



    }
